echo "Dumping a copy of local database $DATABASENAME with prefix $TABLE_PREFIX tables $TABLES_SELECTED to: $LOCAL_TEMP_DIR, extra options:  $NODATA $LOCKTABLES $DATABASES $DROPDB $CHARACTER $COMPA_M4 $COMPA_NT..."
mysqldump -u $MYSQL_USER_LOCAL -p$MYSQL_PASS_LOCAL $NODATA $LOCKTABLES $DATABASES $DROPDB $CHARACTER $COMPA_M4 $COMPA_NT $DATABASENAME $TABLES_SELECTED_FOR_DUMP_LINE | gzip -v > $LOCAL_TEMP_DIR/$DATABASENAME$TABLES_SELECTED-local.sql.gz
